using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI;  public class BoardImgScript : MonoBehaviour {        public GameObject boardImg;     public GameObject target;      // Start is called before the first frame update     void Start()     {     }      // Update is called once per frame     void Update()     {         MakeBoard();     }      private void MakeBoard()     {         var boardPanel = UIManager.Instance.popupBoard.GetComponent<BoardScript>();           // ?쇱そ 留덉슦??踰꾪듉???대┃?덉쓣??         if (Input.GetMouseButtonDown(0))  //0?대㈃ 醫뚰겢由? 1?대㈃ ?고겢由? 2?대㈃ 以묒븰???대┃         {             target = GetClickedObject();             if (boardImg.Equals(target))             {                 boardPanel.show();             }         }     }      private GameObject GetClickedObject()     {         RaycastHit hit;         GameObject target = null;         Ray ray = Camera.main.ScreenPointToRay(Input.mousePosition); //留덉슦???ъ씤??洹쇱쿂 醫뚰몴瑜?留뚮뱺??           if (true == (Physics.Raycast(ray.origin, ray.direction * 10, out hit)))   //留덉슦??洹쇱쿂???ㅻ툕?앺듃媛 ?덈뒗吏 ?뺤씤         {             //?덉쑝硫??ㅻ툕?앺듃瑜???ν븳??             target = hit.collider.gameObject;          }         return target;     } } 